(function() {
  var expect, html, parse, render;

  parse = require('../').parse;

  render = require('../').render;

  expect = require('expect.js');

  /*
    
    TODO: Write tests for render
  */

  /*
    Examples
  */

  /*
    Tests
  */

  html = function(str, options) {
    var dom;
    if (options == null) options = {};
    dom = parse(str, options);
    return render(dom);
  };

  describe('render', function() {
    return describe('(html)', function() {
      it('should render <br /> tags correctly', function(done) {
        var str;
        str = "<br />";
        expect(html(str)).to.equal('<br/>');
        return done();
      });
      it('should shorten the \'checked\' attribute when it contains the value \'checked\'', function(done) {
        var str;
        str = '<input checked/>';
        expect(html(str)).to.equal('<input checked/>');
        return done();
      });
      it('should not shorten the \'name\' attribute when it contains the value \'name\'', function(done) {
        var str;
        str = '<input name = "name"/>';
        expect(html(str)).to.equal('<input name="name"/>');
        return done();
      });
      it('should render comments correctly', function(done) {
        var str;
        str = '<!-- comment -->';
        expect(html(str)).to.equal('<!-- comment -->');
        return done();
      });
      it('should render whitespace by default', function(done) {
        var str;
        str = '<a href="./haha.html">hi</a> <a href="./blah.html">blah</a>';
        expect(html(str)).to.equal(str);
        return done();
      });
      return it('should ignore whitespace if specified', function(done) {
        var str;
        str = '<a href="./haha.html">hi</a> <a href="./blah.html">blah  </a>';
        expect(html(str, {
          ignoreWhitespace: true
        })).to.equal('<a href="./haha.html">hi</a><a href="./blah.html">blah  </a>');
        return done();
      });
    });
  });

}).call(this);
