(function() {

  global.testClass = function(description, specDefinitions) {
    var suite;
    suite = jasmine.getEnv().describe('Class: ' + description, specDefinitions);
    suite.tags = ['class'];
    suite.isIntermediate = true;
    return suite;
  };

  global.feature = function(description, specDefinitions) {
    var suite;
    suite = jasmine.getEnv().describe('Feature: ' + description, specDefinitions);
    suite.tags = ['feature'];
    suite.isIntermediate = true;
    return suite;
  };

  global.scenario = function(desc, func) {
    var suite;
    suite = jasmine.getEnv().describe('Scenario: ' + desc, func);
    suite.tags = ['scenario'];
    suite.isIntermediate = true;
    return suite;
  };

  global.should = function(description, specDefinitions) {
    var suite;
    suite = jasmine.getEnv().it('It should ' + description, specDefinitions);
    suite.tags = ['should'];
    return suite;
  };

}).call(this);
